(()=>{"use strict";(e=>{const t=document.querySelector("#timer-hours"),n=document.querySelector("#timer-minutes"),o=document.querySelector("#timer-seconds"),r=e=>e<10?"0"+e:e,s=()=>{let e=(()=>{let e=(new Date("30 october 2021").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();t.textContent=r(e.hours),n.textContent=r(e.minutes),o.textContent=r(e.seconds);let i=setInterval(s,1e3);e.timeRemaining<0&&(clearInterval(i),t.textContent="00",n.textContent="00",o.textContent="00")};s()})()})();